const update = $json;
let chatId, text = '';

if (update.message) {
  chatId = update.message.chat.id;
  text = update.message.text || '';
} else if (update.callback_query) {
  chatId = update.callback_query.message.chat.id;
  text = update.callback_query.data || '';
}

return [{ json: { chatId, text, raw: update } }];

const data = $getWorkflowStaticData('global');
const catalog = data.catalog;
const incomingText = String($json?.text || '').trim();
const isReloadCommand = incomingText === '/reload';
let memoryEmpty = true;

if (catalog && Object.keys(catalog).length > 0) {
  let hasOptions = false;

  for (const model of Object.values(catalog)) {
    if (!model || !Array.isArray(model.steps)) continue;
    for (const step of model.steps) {
      if (!step || !Array.isArray(step.options)) continue;
      if (step.options.length > 0) {
        hasOptions = true;
        break;
      }
    }
    if (hasOptions) break;
  }

  memoryEmpty = !hasOptions;
}

if (isReloadCommand) memoryEmpty = true;

const items = $input.all();
return items.map(item => ({ json: { ...item.json, memoryEmpty, shouldReload: isReloadCommand, isReloadCommand } }));

const toNum = (v) => {
  if (v === null || v === undefined) return 0;
  const s = String(v).replace(/[^0-9.\-]/g, '');
  const n = parseFloat(s);
  return Number.isFinite(n) ? n : 0;
};
const toInt = (v, d=0) => {
  const n = parseInt(String(v), 10);
  return Number.isFinite(n) ? n : d;
};
const normKey = (k) => String(k || '').toLowerCase().trim().replace(/\s+/g,'').replace(/[_\-]/g,'');
const getVal = (obj, terms) => {
  if (!obj) return undefined;
  const keys = Object.keys(obj);
  const map = new Map(keys.map(k => [normKey(k), k]));
  for (const t of terms) {
    const nk = normKey(t);
    if (map.has(nk)) return obj[map.get(nk)];
  }
  for (const t of terms) {
    const nk = normKey(t);
    const partial = keys.find(k => normKey(k).includes(nk));
    if (partial) return obj[partial];
  }
  return undefined;
};
const norm = (v) => String(v ?? '').trim().replace(/\s+/g, ' ');
const normLower = (v) => norm(v).toLowerCase();
const optionSeen = new WeakMap();
const maquinasRows = $items('Leer DB_Maquinas').map(i => i.json);
const opcionesRows = $items('Leer DB_Opciones').map(i => i.json);
const preciosRows = $items('Leer DB_Precios').map(i => i.json);
const catalog = {};
const priceLookup = new Map();
const basePriceOptionsByModel = new Map();
const priceKey = (modelo, paso, opcion) => `${normLower(modelo)}||${normLower(paso)}||${normLower(opcion)}`;
const isBaseStep = (s) => {
  const x = normLower(s);
  return !x || ['base','baseprice','precio_base','preciobase','pricebase','base_price'].includes(x);
};

for (const row of preciosRows) {
  const modelo = getVal(row, ['modelo','model','maquina','maquina_id']);
  const pasoId = getVal(row, ['paso_id','step_id','paso','step']);
  const opcion = getVal(row, ['opcion','opcion_label','opcion_valor','label','value','nombre_opcion']);
  const precio = toNum(getVal(row, ['precio','price','costo','opcion_precio','extra']));
  if (!modelo || !opcion) continue;

  if (isBaseStep(pasoId)) {
    const modelKey = String(modelo);
    const arr = basePriceOptionsByModel.get(modelKey) || [];
    arr.push({ key: `BP_${arr.length + 1}`, label: String(opcion), price: precio });
    basePriceOptionsByModel.set(modelKey, arr);
    continue;
  }

  priceLookup.set(priceKey(modelo, pasoId, opcion), precio);
}

for (const row of maquinasRows) {
  const modelo = getVal(row, ['modelo','model','id','maquina_id']);
  if (!modelo) continue;
  const nombre = getVal(row, ['nombre','name','descripcion','description']) ?? modelo;
  const basePrice = toNum(getVal(row, ['precio_base','base_price','price','precio','costo']));
  const plantilla = getVal(row, ['plantilla','template','docx','archivo']);
  const modelId = String(modelo);
  const fromSheet = (basePriceOptionsByModel.get(modelId) || []).filter(p => Number.isFinite(p.price));
  const defaultOption = [{ key: 'BASE', label: 'Precio base', price: basePrice }];
  const priceOptions = fromSheet.length > 0 ? fromSheet : defaultOption;

  catalog[modelId] = {
    id: modelId,
    name: String(nombre),
    basePrice,
    priceOptions,
    plantilla: plantilla ? String(plantilla) : undefined,
    steps: []
  };
}

for (const row of opcionesRows) {
  const modelo = getVal(row, ['modelo','model','maquina','maquina_id']);
  if (!modelo) continue;
  const m = catalog[String(modelo)];
  if (!m) continue;
  const pasoId = getVal(row, ['paso_id','step_id','paso','step']);
  const pregunta = getVal(row, ['pregunta','paso_pregunta','question','texto']);
  const orden = toInt(getVal(row, ['paso_orden','order','orden']), 0);
  if (!pasoId) continue;
  let step = m.steps.find(s => s.id === String(pasoId));
  if (!step) {
    const questionStr = norm(pregunta);
    step = { id: String(pasoId), question: questionStr || 'Selecciona una opci√≥n', order: orden, options: [] };
    m.steps.push(step);
  }
  const rawLabel = getVal(row, ['opcion_label','label','etiqueta','nombre_opcion','opcion']) || getVal(row, ['opcion label','opcion_label','opcionlabel']);
  const rawValue = getVal(row, ['opcion_valor','value','valor','code','key']) || rawLabel;
  const fallbackPrice = toNum(getVal(row, ['opcion_precio','price','precio','costo','extra']));
  const labelStr = norm(rawLabel || rawValue);
  let valueStr = norm(rawValue || rawLabel || labelStr);
  if (labelStr === '') continue;
  if (valueStr === '') valueStr = labelStr;
  const modelKey = String(modelo);
  const priceFromSheet = priceLookup.get(priceKey(modelKey, pasoId, valueStr)) ?? priceLookup.get(priceKey(modelKey, pasoId, labelStr));
  const price = Number.isFinite(priceFromSheet) ? priceFromSheet : fallbackPrice;
  const key = `${normLower(labelStr)}|${normLower(valueStr)}`;
  let seenForStep = optionSeen.get(step);
  if (!seenForStep) { seenForStep = new Set(); optionSeen.set(step, seenForStep); }
  if (seenForStep.has(key)) continue;
  seenForStep.add(key);
  step.options.push({ label: labelStr, value: valueStr, price });
}

for (const mid of Object.keys(catalog)) {
  catalog[mid].steps.sort((a,b) => (a.order ?? 0) - (b.order ?? 0));
}

const input = $input.first().json;
const staticData = $getWorkflowStaticData('global');
staticData.catalog = catalog;
return [{ json: { ...input, catalogLoaded: true, models: Object.keys(catalog).length } }];

const { chatId, text, raw, shouldReload: inReload } = $json;
const staticData = $getWorkflowStaticData('global');
staticData.cot = staticData.cot || {};
const hasSession = !!staticData.cot?.[chatId];
let action = null;
const incomingText = String(text || '').trim();
const isReloadCommand = incomingText === '/reload';
const shouldReload = !!inReload || isReloadCommand;

const setMode = (mode) => {
  if (!mode) return;
  const current = staticData.cot[chatId] || {};
  staticData.cot[chatId] = { ...current, mode: mode.toUpperCase() };
};

if (raw?.message?.text && raw.message.text.startsWith('/')) {
  action = 'START';
  staticData.cot[chatId] = {};
} else if (raw?.callback_query?.data) {
  const data = raw.callback_query.data;
  if (data === 'MENU|AVANZADA' || data === 'MODE|ADV') {
    setMode('ADV');
    action = 'MENU_SELECTION';
  } else if (data === 'MODE|FAST') {
    setMode('FAST');
    action = 'MENU_SELECTION';
  } else {
    action = 'COTIZACION_FLOW';
  }
} else if (raw?.message?.text && !raw.message.text.startsWith('/') && hasSession) {
  action = 'COTIZACION_FLOW';
}

return [{ json: { chatId, text, action, shouldReload, isReloadCommand } }];

const staticData = $getWorkflowStaticData('global');
const catalog = staticData.catalog || {};
const models = Object.keys(catalog);
if (models.length === 0) {
  return [{ json: { chatId: $json.chatId, text: '‚ö†Ô∏è La memoria est√° vac√≠a. Por favor ejecuta /start para recargar.', reply_markup: {} } }];
}
const keyboard = [];
let row = [];
models.forEach((model, index) => {
  row.push({ text: model, callback_data: `${model}|START` });
  if (row.length === 2 || index === models.length - 1) { keyboard.push(row); row = []; }
});
const prefix = ($json.isReloadCommand || $json.shouldReload) ? '‚úÖ Cat√°logo actualizado\n\n' : '';
return [{ json: { chatId: $json.chatId, text: `${prefix}üè≠ *Cat√°logo VC999 Disponible*\n\nSelecciona la m√°quina que deseas cotizar:`, reply_markup: { inline_keyboard: keyboard } } }];

const DEBUG = false;
const staticData = $getWorkflowStaticData('global');
const catalog = staticData.catalog || {};
staticData.cot = staticData.cot || {};
const chatId = $json.chatId ?? $json.raw?.message?.chat?.id ?? $json.raw?.callback_query?.message?.chat?.id;
const text = $json.text ?? '';
if (!chatId) {
  return [{ json: { ok: false, error: 'missing_chatId', text: '‚ö†Ô∏è Falta chatId. Revisa el nodo Normalize Telegram.', reply_markup: {}, isFinished: true, isReadyToGenerate: false, quotePayload: null, userState: null } }];
}
const money = (n) => Number(n || 0).toLocaleString(undefined, { minimumFractionDigits: 0, maximumFractionDigits: 0 });
const toNum = (v) => { const s = String(v ?? '').replace(/[^0-9.\-]/g, ''); const n = parseFloat(s); return Number.isFinite(n) ? n : 0; };
const normText = (v) => String(v ?? '').trim().replace(/\s+/g, ' ');
const optionKey = (label, value) => `${normText(label).toLowerCase()}|${normText(value ?? label).toLowerCase()}`;
const humanizeStepId = (stepId) => {
  const safe = String(stepId ?? '').trim();
  const withSpaces = safe.replace(/[_-]+/g, ' ').replace(/([a-z])([A-Z])/g, '$1 $2').trim();
  if (!withSpaces) return 'Paso';
  return withSpaces.split(' ').filter(Boolean).map(word => word.charAt(0).toUpperCase() + word.slice(1).toLowerCase()).join(' ');
};
const resolveMode = (state) => (state?.mode || 'ADV').toUpperCase();
const formatSelections = (selections) => Array.isArray(selections) && selections.length > 0 ? selections.map((sel) => { const stepLabel = humanizeStepId(sel.step ?? 'Paso'); const priceText = toNum(sel.price) > 0 ? ` (+$${money(sel.price)} USD)` : ' (+$0 USD)'; return `- ${stepLabel}: ${sel.value ?? 'N/A'}${priceText}`; }) : ['- Sin opciones'];
const startFastDefaults = (userState, machine) => {
  const steps = Array.isArray(machine.steps) ? machine.steps : [];
  const selections = [];
  let totalPrice = toNum(userState.basePrice);
  steps.forEach((step, idx) => {
    const options = Array.isArray(step?.options) ? step.options : [];
    if (options.length === 0) return;
    const defaultOpt = options.find((opt) => String(opt.label ?? '').includes('(Default)')) || options[0];
    const chosenPrice = toNum(defaultOpt.price);
    selections.push({ step: step?.id ?? `step_${idx + 1}`, value: defaultOpt.value ?? defaultOpt.label ?? 'NA', price: chosenPrice });
    totalPrice += chosenPrice;
  });
  userState.selections = selections;
  userState.totalPrice = totalPrice;
  userState.stepIndex = steps.length;
  userState.stage = 'await_customer_name';
  return { text: '‚ö°Ô∏è *Modo R√°pido activado*\nSe seleccionaron las opciones por defecto. Escribe el *nombre del cliente*.', reply_markup: {} };
};
let userState = staticData.cot[chatId];
const isCallback = typeof text === 'string' && text.includes('|');
const isStartMachine = isCallback && text.endsWith('|START');
const isOpt = isCallback && text.includes('|OPT|');
const isBasePick = isCallback && text.includes('|BP|');
let fastIntroResponse = null;

if (isStartMachine) {
  const model = text.split('|')[0];
  const machine = catalog[model];
  if (!machine) return [{ json: { chatId, text: '‚ö†Ô∏è Error: M√°quina no encontrada. Ejecuta /start.', reply_markup: {}, isFinished: true, isReadyToGenerate: false, quotePayload: null, userState: staticData.cot[chatId] || null } }];
  const mode = resolveMode(userState);
  const options = Array.isArray(machine.priceOptions) && machine.priceOptions.length ? machine.priceOptions : [{ key: 'BASE', label: 'Precio base', price: toNum(machine.basePrice) }];
  userState = { mode, stage: 'config', machine: model, stepIndex: 0, selections: [], basePrice: toNum(options[0]?.price ?? machine.basePrice), totalPrice: toNum(options[0]?.price ?? machine.basePrice), customer: {}, advisor: '' };

  if (options.length > 1) {
    userState.stage = 'pick_base_price';
    const inline_keyboard = options.map((opt, idx) => [{ text: `${opt.label} ($${money(opt.price)} USD)`, callback_data: `${model}|BP|${idx}` }]);
    staticData.cot[chatId] = userState;
    return [{ json: { chatId, text: `üí∞ Selecciona el precio base para *${model}*:`, reply_markup: { inline_keyboard }, isFinished: true, isReadyToGenerate: false, quotePayload: null, userState } }];
  }

  if (mode === 'FAST') fastIntroResponse = startFastDefaults(userState, machine);
}

if (isBasePick) {
  const [model, , idxRaw] = text.split('|');
  const machine = catalog[model];
  if (!userState || !machine || userState.machine !== model) return [{ json: { chatId, text: '‚ö†Ô∏è Sesi√≥n expirada o modelo no coincide. Ejecuta /start.', reply_markup: {}, isFinished: true, isReadyToGenerate: false, quotePayload: null, userState: staticData.cot[chatId] || null } }];
  const options = Array.isArray(machine.priceOptions) && machine.priceOptions.length ? machine.priceOptions : [{ key: 'BASE', label: 'Precio base', price: toNum(machine.basePrice) }];
  const idx = Math.max(0, Math.min(options.length - 1, Number(idxRaw)));
  const selected = options[idx] || options[0];
  userState.basePrice = toNum(selected.price);
  userState.totalPrice = toNum(selected.price);
  userState.stage = 'config';
  if (userState.mode === 'FAST') fastIntroResponse = startFastDefaults(userState, machine);
}

if (isOpt) {
  const parts = text.split('|');
  const model = parts[0];
  const stepId = parts[2];
  const val = parts[3];
  const price = toNum(parts[4]);
  if (!userState || userState.machine !== model) return [{ json: { chatId, text: '‚ö†Ô∏è Sesi√≥n expirada o modelo no coincide. Ejecuta /start.', reply_markup: {}, isFinished: true, isReadyToGenerate: false, quotePayload: null, userState: staticData.cot[chatId] || null } }];
  if (userState.mode === 'FAST') return [{ json: { chatId, text: '‚ö°Ô∏è Est√°s en Modo R√°pido. Las opciones ya fueron auto-seleccionadas. Env√≠a el nombre del cliente.', reply_markup: {}, isFinished: true, isReadyToGenerate: false, quotePayload: null, userState } }];
  userState.selections.push({ step: stepId, value: val, price });
  userState.totalPrice += price;
  userState.stepIndex += 1;
}

if (!isCallback && userState) {
  const msg = normText(text || '');
  if (userState.stage === 'await_customer_name') { userState.customer.name = msg; userState.stage = 'await_customer_email'; staticData.cot[chatId] = userState; return [{ json: { chatId, text: 'üìù Ahora escribe el *correo del cliente*:', reply_markup: {}, isFinished: true, isReadyToGenerate: false, quotePayload: null, userState } }]; }
  if (userState.stage === 'await_customer_email') { userState.customer.email = msg; userState.stage = 'await_advisor'; staticData.cot[chatId] = userState; return [{ json: { chatId, text: 'üßë‚Äçüíº Ahora escribe el *nombre del ASESOR*:', reply_markup: {}, isFinished: true, isReadyToGenerate: false, quotePayload: null, userState } }]; }
  if (userState.stage === 'await_advisor') {
    userState.advisor = msg; userState.stage = 'done';
    const payload = { machine: userState.machine, basePrice: toNum(userState.basePrice), totalPrice: toNum(userState.totalPrice), selections: userState.selections, customer: userState.customer, advisor: userState.advisor };
    userState.payloadFinal = payload; staticData.cot[chatId] = userState;
    const optionLines = formatSelections(userState.selections).join('\n');
    const summaryText = `üõ†Ô∏è *Cotizaci√≥n ${userState.machine}*\n*M√°quina:* ${userState.machine}\n*Base:* $${money(payload.basePrice)} USD\n*Opciones:*\n${optionLines}\n*Total:* $${money(payload.totalPrice)} USD\n*Cliente:* ${userState.customer.name || 'N/A'}\n*Correo:* ${userState.customer.email || 'N/A'}\n*Asesor:* ${userState.advisor || 'N/A'}\n‚úÖ Confirmo datos. Procediendo a generar y enviar PDF.`;
    return [{ json: { chatId, text: summaryText, reply_markup: {}, isFinished: true, isReadyToGenerate: true, quotePayload: payload, userState } }];
  }
}

if (!userState) return [{ json: { chatId, text: 'Usa /start y selecciona una m√°quina para iniciar.', reply_markup: {}, isFinished: true, isReadyToGenerate: false, quotePayload: null, userState: null } }];
const machineConfig = catalog[userState.machine];
if (!machineConfig) return [{ json: { chatId, text: '‚ö†Ô∏è Cat√°logo no disponible. Ejecuta /start para recargar.', reply_markup: {}, isFinished: true, isReadyToGenerate: false, quotePayload: null, userState } }];

let response = fastIntroResponse || {};
let isFinished = !!fastIntroResponse;
if (!isFinished && userState.stage === 'config') {
  const currentIndex = userState.stepIndex;
  if (currentIndex < machineConfig.steps.length) {
    const step = machineConfig.steps[currentIndex];
    const stepOptions = Array.isArray(step.options) ? step.options : [];
    if (stepOptions.length === 0) {
      const debugInfo = DEBUG ? ` [model=${userState.machine}, step=${step.id ?? currentIndex + 1}]` : '';
      response = { text: `‚ö†Ô∏è No hay opciones disponibles para el paso "${step.id ?? currentIndex + 1}". Usa /start para recargar.${debugInfo}`, reply_markup: {} };
    } else {
      const inline_keyboard = [];
      const seenOptions = new Set();
      stepOptions.forEach(opt => {
        const price = toNum(opt.price);
        const value = opt.value ?? opt.label ?? 'NA';
        const key = optionKey(opt.label ?? value, value);
        if (seenOptions.has(key)) return;
        seenOptions.add(key);
        const priceText = price > 0 ? ` (+$${money(price)})` : '';
        inline_keyboard.push([{ text: `${opt.label ?? value}${priceText}`, callback_data: `${userState.machine}|OPT|${step.id}|${value}|${price}` }]);
      });
      response = { text: `üõ†Ô∏è *${userState.machine}* (Paso ${currentIndex + 1}/${machineConfig.steps.length})\nüí∞ Total: $${money(userState.totalPrice)} USD\n\nSelecciona: *${step.question || humanizeStepId(step.id || `Paso ${currentIndex + 1}`)}*`, reply_markup: { inline_keyboard } };
    }
  } else {
    userState.stage = 'await_customer_name';
    isFinished = true;
    response = { text: `‚úÖ *Cotizaci√≥n lista*\nModelo: *${userState.machine}*\nTotal: *$${money(userState.totalPrice)} USD*\n\nEscribe el *NOMBRE del Cliente*:`, reply_markup: {} };
  }
} else if (userState.stage === 'await_customer_name') { isFinished = true; response = { text: 'üìù Ahora escribe el *NOMBRE del cliente*:', reply_markup: {} }; }
else if (userState.stage === 'await_customer_email') { isFinished = true; response = { text: 'üìù Ahora escribe el *correo del cliente*:', reply_markup: {} }; }
else if (userState.stage === 'await_advisor') { isFinished = true; response = { text: 'üßë‚Äçüíº Ahora escribe el *nombre del ASESOR*:', reply_markup: {} }; }
else if (userState.stage === 'done') { const payload = userState.payloadFinal || { machine: userState.machine, basePrice: toNum(userState.basePrice), totalPrice: toNum(userState.totalPrice), selections: userState.selections, customer: userState.customer, advisor: userState.advisor || '' }; response = { text: '‚úÖ Listo. Procediendo a generar y enviar PDF.', reply_markup: {}, quotePayload: payload, isReadyToGenerate: true }; isFinished = true; }

staticData.cot[chatId] = userState;
return [{ json: { chatId, text: response.text || '', reply_markup: response.reply_markup || {}, isFinished: !!isFinished, isReadyToGenerate: !!(response.quotePayload), quotePayload: response.quotePayload || null, userState } }];

const input = $items('¬øTermin√≥?')[0]?.json ?? $json;
const userState = input.userState || {};
const customer = userState.customer || {};
const toNum = (v) => { const s = String(v ?? '').replace(/[^0-9.\-]/g, ''); const n = parseFloat(s); return Number.isFinite(n) ? n : 0; };
const normKey = (k) => String(k || '').toLowerCase().trim().replace(/\s+/g,'').replace(/[_\-]/g,'');
const normLower = (v) => String(v ?? '').trim().toLowerCase();
const getVal = (obj, terms) => { if (!obj) return undefined; const keys = Object.keys(obj); const map = new Map(keys.map(k => [normKey(k), k])); for (const t of terms) { const nk = normKey(t); if (map.has(nk)) return obj[map.get(nk)]; } for (const t of terms) { const nk = normKey(t); const partial = keys.find(k => normKey(k).includes(nk)); if (partial) return obj[partial]; } return undefined; };
const machine = userState.machine ?? input.quotePayload?.machine ?? '';
const machineKey = String(machine ?? '').trim().toLowerCase();
const maquinasRows = $items('Leer DB_Maquinas Precio Base').map((i) => i.json);
const preciosRows = $items('Leer DB_Precios FastAPI').map((i) => i.json);
let basePriceFromSheet;
for (const row of maquinasRows) { const modelo = getVal(row, ['modelo','model','id','maquina_id']); if (!modelo) continue; if (String(modelo).trim().toLowerCase() === machineKey) { basePriceFromSheet = toNum(getVal(row, ['precio_base','base_price','price','precio','costo'])); break; } }
const resolvedBasePrice = Number.isFinite(Number(userState.basePrice)) ? Number(userState.basePrice) : (Number.isFinite(basePriceFromSheet) ? basePriceFromSheet : Number(userState.totalPrice ?? 0));
const priceLookup = new Map();
const priceKey = (modelo, paso, opcion) => `${normLower(modelo)}||${normLower(paso)}||${normLower(opcion)}`;
for (const row of preciosRows) { const modelo = getVal(row, ['modelo','model','maquina','maquina_id']); const pasoId = getVal(row, ['paso_id','step_id','paso','step']); const opcion = getVal(row, ['opcion','opcion_label','opcion_valor','label','value','nombre_opcion']); const precio = toNum(getVal(row, ['precio','price','costo','opcion_precio','extra'])); if (!modelo || !pasoId || !opcion) continue; priceLookup.set(priceKey(modelo, pasoId, opcion), precio); }
const selections = Array.isArray(userState.selections) ? userState.selections.map((sel) => { const paso = sel.step ?? sel.label ?? ''; const opcion = sel.value ?? sel.option ?? ''; const sheetPrice = priceLookup.get(priceKey(machine, paso, opcion)); const precio = Number.isFinite(sheetPrice) ? sheetPrice : Number(sel.price ?? 0); return { paso, opcion, precio }; }) : [];
const selectionsTotal = selections.reduce((sum, sel) => sum + toNum(sel.precio), 0);
const resolvedTotalPrice = resolvedBasePrice + selectionsTotal;
const fastapiBody = { modelo: machine ?? '', nombre_cliente: customer.name ?? '', email: customer.email ?? '', asesor: userState.advisor ?? '', precio_base: resolvedBasePrice, precio_cambiado: resolvedTotalPrice, selecciones: selections };
return [{ json: { ...input, fastapiBody } }];

