const update = $json;
let chatId, text = '';
let raw = update;
let callbackQueryId = null;
let callbackMessageId = null;
let isCallback = false;

if (update.message) {
  chatId = update.message.chat.id;
  text = update.message.text || '';
} else if (update.callback_query) {
  isCallback = true;
  callbackQueryId = update.callback_query.id;
  chatId = update.callback_query.message.chat.id;
  callbackMessageId = update.callback_query.message.message_id;
  text = update.callback_query.data || '';
}

return [{ json: { chatId, text, raw, isCallback, callbackQueryId, callbackMessageId } }];

const data = $getWorkflowStaticData('global');
const incomingText = String($json?.text || '').trim();
const isReloadCommand = incomingText.startsWith('/reload');
const isStartCommand = incomingText.startsWith('/start');

const hasCatalog = !!data.catalog && Object.keys(data.catalog).length > 0;
const hasPriceIndex = !!data.priceIndex && Object.keys(data.priceIndex).length > 0;

const needsCatalogReload = !hasCatalog;
const needsPricesReload = isStartCommand || isReloadCommand || !hasPriceIndex;
const needsFullReload = isReloadCommand || needsCatalogReload;

const firstItem = $input.first()?.json ?? {};
return [{
  json: {
    ...firstItem,
    needsCatalogReload,
    needsPricesReload,
    needsFullReload,
    shouldReload: isReloadCommand,
    isReloadCommand,
    isStartCommand,
  },
}];

const toNum = (v) => {
  if (v === null || v === undefined) return 0;
  const s = String(v).replace(/,/g, '').replace(/[^0-9.\-]/g, '');
  const n = parseFloat(s);
  return Number.isFinite(n) ? n : 0;
};
const toInt = (v, d = 0) => {
  const n = parseInt(String(v), 10);
  return Number.isFinite(n) ? n : d;
};
const normKey = (k) => String(k || '').toLowerCase().trim().replace(/\s+/g, '').replace(/[_\-]/g, '');
const getVal = (obj, terms) => {
  if (!obj) return undefined;
  const keys = Object.keys(obj);
  const map = new Map(keys.map(k => [normKey(k), k]));
  for (const t of terms) {
    const nk = normKey(t);
    if (map.has(nk)) return obj[map.get(nk)];
  }
  for (const t of terms) {
    const nk = normKey(t);
    const partial = keys.find(k => normKey(k).includes(nk));
    if (partial) return obj[partial];
  }
  return undefined;
};
const norm = (v) => String(v ?? '').trim().replace(/\s+/g, ' ');
const normUpper = (v) => norm(v).toUpperCase();
const stripPrice = (s) => {
  const raw = String(s ?? '');
  return raw
    .replace(/\([^)]*\$[\s\d.,]+[^)]*\)/g, '')
    .replace(/\+?\$[\s\d.,]+(\s*USD)?/gi, '')
    .replace(/\s{2,}/g, ' ')
    .trim();
};
const normOption = (v) => norm(stripPrice(v));
const normStep = (v) => norm(v);
const isActive = (row) => {
  const activeRaw = getVal(row, ['activo', 'active', 'enabled', 'habilitado']);
  if (activeRaw === undefined || activeRaw === null || String(activeRaw).trim() === '') return true;
  const txt = String(activeRaw).trim().toLowerCase();
  return ['1', 'true', 'verdadero', 'si', 's√≠', 'yes', 'y', 'on'].includes(txt);
};
const priceKey = (modelo, pasoId, opcionValor) => `${normUpper(modelo)}|${normStep(pasoId)}|${normOption(opcionValor)}`;

const input = $input.first()?.json ?? {};
const maquinasRows = Array.isArray(input.maquinasRows) ? input.maquinasRows.filter(isActive) : [];
const opcionesRows = Array.isArray(input.opcionesRows) ? input.opcionesRows.filter(isActive) : [];
const preciosRows = Array.isArray(input.preciosRows) ? input.preciosRows.filter(isActive) : [];

const staticData = $getWorkflowStaticData('global');
const existingCatalog = staticData.catalog || {};
const catalog = input.needsFullReload ? {} : JSON.parse(JSON.stringify(existingCatalog));
const basePriceIndex = input.needsFullReload ? {} : { ...(staticData.basePriceIndex || {}) };
const priceIndex = {};

for (const row of maquinasRows) {
  const modelo = getVal(row, ['modelo', 'model', 'id', 'maquina_id']);
  if (!modelo) continue;
  const modelId = normUpper(modelo);
  const nombre = getVal(row, ['nombre', 'name', 'descripcion', 'description']) ?? modelId;
  const basePrice = toNum(getVal(row, ['precio_base', 'base_price', 'price', 'precio', 'costo']));
  const plantilla = getVal(row, ['plantilla', 'template', 'docx', 'archivo']);

  basePriceIndex[modelId] = basePrice;
  if (!catalog[modelId]) {
    catalog[modelId] = {
      id: modelId,
      name: String(nombre),
      basePrice,
      priceOptions: [{ key: 'BASE', label: 'Precio base', price: basePrice }],
      plantilla: plantilla ? String(plantilla) : undefined,
      steps: [],
    };
  } else {
    catalog[modelId].basePrice = basePrice;
    catalog[modelId].name = String(nombre || catalog[modelId].name || modelId);
    catalog[modelId].priceOptions = [{ key: 'BASE', label: 'Precio base', price: basePrice }];
    if (plantilla) catalog[modelId].plantilla = String(plantilla);
  }
}

for (const row of preciosRows) {
  const modelo = getVal(row, ['modelo', 'model', 'maquina', 'maquina_id']);
  const pasoId = getVal(row, ['paso_id', 'step_id', 'paso', 'step']);
  const opcionValor = getVal(row, ['opcion_valor', 'value', 'valor', 'opcion', 'opcion_label', 'label', 'nombre_opcion']);
  const precio = toNum(getVal(row, ['precio_extra_us', 'precio', 'price', 'costo', 'opcion_precio', 'extra']));
  if (!modelo || !pasoId || !opcionValor) continue;
  const key = priceKey(modelo, pasoId, opcionValor);
  priceIndex[key] = precio;
}

if (input.needsFullReload) {
  for (const modelId of Object.keys(catalog)) {
    catalog[modelId].steps = [];
  }

  for (const row of opcionesRows) {
    const modelo = getVal(row, ['modelo', 'model', 'maquina', 'maquina_id']);
    if (!modelo) continue;
    const modelId = normUpper(modelo);
    const m = catalog[modelId];
    if (!m) continue;

    const pasoIdRaw = getVal(row, ['paso_id', 'step_id', 'paso', 'step']);
    if (!pasoIdRaw) continue;
    const pasoId = normStep(pasoIdRaw);
    const pregunta = getVal(row, ['pregunta', 'paso_pregunta', 'question', 'texto']);
    const orden = toInt(getVal(row, ['paso_orden', 'order', 'orden']), 0);

    let step = m.steps.find(s => s.id === pasoId);
    if (!step) {
      step = { id: pasoId, question: norm(pregunta) || 'Selecciona una opci√≥n', order: orden, options: [] };
      m.steps.push(step);
    }

    const rawLabel = getVal(row, ['opcion_label', 'label', 'etiqueta', 'nombre_opcion', 'opcion']) || getVal(row, ['opcion label', 'opcion_label', 'opcionlabel']);
    const rawValue = getVal(row, ['opcion_valor', 'value', 'valor', 'code', 'key']) || rawLabel;
    const fallbackPrice = toNum(getVal(row, ['opcion_precio', 'price', 'precio', 'costo', 'extra']));

    const labelClean = normOption(rawLabel || rawValue);
    const valueClean = normOption(rawValue || rawLabel || labelClean);
    if (!labelClean || !valueClean) continue;

    const fromIndex = priceIndex[priceKey(modelId, pasoId, valueClean)] ?? priceIndex[priceKey(modelId, pasoId, labelClean)];
    const priceFinal = Number.isFinite(Number(fromIndex)) ? Number(fromIndex) : fallbackPrice;

    const dedupe = `${labelClean.toLowerCase()}|${valueClean.toLowerCase()}`;
    const seen = step.__seen || (step.__seen = new Set());
    if (seen.has(dedupe)) continue;
    seen.add(dedupe);

    step.options.push({ label: labelClean, value: valueClean, price: priceFinal });
  }

  for (const modelId of Object.keys(catalog)) {
    catalog[modelId].steps.sort((a, b) => (a.order ?? 0) - (b.order ?? 0));
    for (const step of catalog[modelId].steps) delete step.__seen;
  }
} else {
  for (const modelId of Object.keys(catalog)) {
    const machine = catalog[modelId];
    if (!Array.isArray(machine?.steps)) continue;
    for (const step of machine.steps) {
      if (!Array.isArray(step?.options)) continue;
      step.options = step.options.map((opt) => {
        const labelClean = normOption(opt?.label ?? opt?.value);
        const valueClean = normOption(opt?.value ?? opt?.label);
        const key = priceKey(modelId, step.id, valueClean);
        const keyLabel = priceKey(modelId, step.id, labelClean);
        const nextPrice = priceIndex[key] ?? priceIndex[keyLabel];
        return {
          ...opt,
          label: labelClean,
          value: valueClean,
          price: Number.isFinite(Number(nextPrice)) ? Number(nextPrice) : toNum(opt?.price),
        };
      });
    }
  }
}

staticData.catalog = catalog;
staticData.basePriceIndex = basePriceIndex;
staticData.priceIndex = priceIndex;
staticData.pricesLoadedAt = new Date().toISOString();

return [{
  json: {
    ...input,
    catalogLoaded: true,
    models: Object.keys(catalog).length,
    pricesLoadedAt: staticData.pricesLoadedAt,
  }
}];

const { chatId, text, raw, shouldReload: inReload, isCallback, callbackQueryId, callbackMessageId } = $json;
const staticData = $getWorkflowStaticData('global');
staticData.cot = staticData.cot || {};
const hasSession = !!staticData.cot?.[chatId];
let action = null;
const incomingText = String(text || '').trim();
const isReloadCommand = incomingText === '/reload';
const shouldReload = !!inReload || isReloadCommand;

const setMode = (mode) => {
  if (!mode) return;
  const current = staticData.cot[chatId] || {};
  staticData.cot[chatId] = { ...current, mode: mode.toUpperCase() };
};

if (isReloadCommand) {
  action = 'RELOAD_DONE';
} else if (raw?.message?.text && raw.message.text.startsWith('/')) {
  action = 'START';
  staticData.cot[chatId] = {};
} else if (raw?.callback_query?.data) {
  const data = raw.callback_query.data;
  if (data === 'MENU|AVANZADA' || data === 'MODE|ADV') {
    setMode('ADV');
    action = 'MENU_SELECTION';
  } else if (data === 'MODE|FAST') {
    setMode('FAST');
    action = 'MENU_SELECTION';
  } else {
    action = 'COTIZACION_FLOW';
  }
} else if (raw?.message?.text && !raw.message.text.startsWith('/') && hasSession) {
  action = 'COTIZACION_FLOW';
}

const outText = action === 'RELOAD_DONE' ? '‚úÖ Cat√°logo actualizado' : text;
return [{ json: { chatId, text: outText, action, shouldReload, isReloadCommand, raw, isCallback, callbackQueryId, callbackMessageId } }];

const staticData = $getWorkflowStaticData('global');
const catalog = staticData.catalog || {};
const models = Object.keys(catalog);
if (models.length === 0) {
  return [{ json: { chatId: $json.chatId, text: '‚ö†Ô∏è La memoria est√° vac√≠a. Por favor ejecuta /start para recargar.', reply_markup: {} } }];
}
const keyboard = [];
let row = [];
models.forEach((model, index) => {
  row.push({ text: model, callback_data: `${model}|START` });
  if (row.length === 2 || index === models.length - 1) { keyboard.push(row); row = []; }
});
return [{ json: { chatId: $json.chatId, text: 'üè≠ *Cat√°logo VC999 Disponible*\n\nSelecciona la m√°quina que deseas cotizar:', reply_markup: { inline_keyboard: keyboard } } }];

const staticData = $getWorkflowStaticData('global');
const catalog = staticData.catalog || {};
const basePriceIndex = staticData.basePriceIndex || {};
const priceIndex = staticData.priceIndex || {};
staticData.cot = staticData.cot || {};

const chatId = $json.chatId;
const text = String($json.text ?? '').trim();
const callbackMessageId = $json.callbackMessageId ?? null;
const callbackQueryId = $json.callbackQueryId ?? null;
const isCallback = !!$json.isCallback;

const money = (n) => Number(n || 0).toLocaleString(undefined, { minimumFractionDigits: 0, maximumFractionDigits: 0 });
const formatPriceUSD = (n) => `$${money(n)}`;
const toNum = (v) => {
  const s = String(v ?? '').replace(/[^0-9.,\-]/g, '').replace(/,/g, '');
  const n = parseFloat(s);
  return Number.isFinite(n) ? n : 0;
};
const normText = (v) => String(v ?? '').trim().replace(/\s+/g, ' ');
const normModel = (v) => normText(v).toUpperCase();
const normStep = (v) => normText(v);
const stripPrice = (s) => {
  const raw = String(s ?? '');
  return raw
    .replace(/\([^)]*\$[\s\d.,]+[^)]*\)/g, '')
    .replace(/\+?\$[\s\d.,]+(\s*USD)?/gi, '')
    .replace(/\s{2,}/g, ' ')
    .trim();
};
const optionKey = (label, value) => `${stripPrice(normText(label)).toLowerCase()}|${stripPrice(normText(value ?? label)).toLowerCase()}`;
const priceKey = (modelo, paso, opcionValor) => `${normModel(modelo)}|${normStep(paso)}|${stripPrice(normText(opcionValor))}`;
const resolvePriceFinal = (modelo, paso, opcionValor, fallback=0) => {
  const key = priceKey(modelo, paso, stripPrice(opcionValor));
  const p = priceIndex[key];
  return Number.isFinite(Number(p)) ? Number(p) : toNum(fallback);
};
const humanizeStepId = (stepId) => {
  const safe = String(stepId ?? '').trim();
  const withSpaces = safe.replace(/[_-]+/g, ' ').replace(/([a-z])([A-Z])/g, '$1 $2').trim();
  if (!withSpaces) return 'Paso';
  return withSpaces.split(' ').filter(Boolean).map((word) => word.charAt(0).toUpperCase() + word.slice(1).toLowerCase()).join(' ');
};

const resolveMode = (state) => (state?.mode || 'ADV').toUpperCase();
const sanitizeMachine = (value) => String(value ?? '').trim().replace(/\.docx$/i, '').toUpperCase();
const ensureRunId = (state) => {
  if (!state.runId) state.runId = `${Date.now()}_${chatId}`;
  return state.runId;
};
const formatSelections = (selections) => Array.isArray(selections) && selections.length > 0
  ? selections.map((sel) => {
      const stepLabel = humanizeStepId(sel.step ?? 'Paso');
      const priceText = toNum(sel.price) > 0 ? ` (+${formatPriceUSD(sel.price)} USD)` : ' (+$0 USD)';
      return `- ${stepLabel}: ${sel.value ?? 'N/A'}${priceText}`;
    })
  : ['- Sin opciones'];

const startFastDefaults = (userState, machine) => {
  const steps = Array.isArray(machine.steps) ? machine.steps : [];
  const selections = [];
  let totalPrice = toNum(userState.basePrice);
  steps.forEach((step, idx) => {
    const options = Array.isArray(step?.options) ? step.options : [];
    if (options.length === 0) return;
    const defaultOpt = options.find((opt) => String(opt.label ?? '').includes('(Default)')) || options[0];
    const selectedValue = defaultOpt.value ?? defaultOpt.label ?? 'NA';
    const chosenPrice = resolvePriceFinal(userState.machine, step?.id ?? `step_${idx + 1}`, selectedValue, defaultOpt.price);
    selections.push({ step: step?.id ?? `step_${idx + 1}`, value: stripPrice(selectedValue), price: chosenPrice });
    totalPrice += chosenPrice;
  });
  userState.selections = selections;
  userState.totalPrice = totalPrice;
  userState.stepIndex = steps.length;
  userState.stage = 'await_customer_name';
  return { text: '‚ö°Ô∏è *Modo R√°pido activado*
Se seleccionaron las opciones por defecto. Escribe el *nombre del cliente*.', reply_markup: {} };
};

const output = (payload) => [{ json: { ...payload, chatId, callbackMessageId, callbackQueryId, isCallback } }];

if (!chatId) {
  return output({ text: '‚ö†Ô∏è Falta chatId. Revisa el nodo Normalize Telegram.', reply_markup: {}, isFinished: true, isReadyToGenerate: false, quotePayload: null, userState: null });
}

let userState = staticData.cot[chatId] || null;
const isStartMachine = text.includes('|') && text.endsWith('|START');
const isBasePick = text.includes('|BP|');
const isOpt = text.includes('|OPT|');

if (isStartMachine) {
  const model = sanitizeMachine(text.split('|')[0]);
  const machine = catalog[model];
  if (!machine) return output({ text: '‚ö†Ô∏è Error: M√°quina no encontrada. Ejecuta /start.', reply_markup: {}, isFinished: true, isReadyToGenerate: false, quotePayload: null, userState });

  const mode = resolveMode(userState);
  const listBasePrice = Number.isFinite(Number(basePriceIndex[model])) ? Number(basePriceIndex[model]) : toNum(machine.basePrice);
  userState = { mode, stage: 'await_base_price_choice', machine: model, stepIndex: 0, selections: [], basePrice: listBasePrice, totalPrice: listBasePrice, customer: {}, advisor: '' };
  ensureRunId(userState);
  staticData.cot[chatId] = userState;

  return output({
    text: `Precio base actual: ${formatPriceUSD(listBasePrice)} USD. ¬øDeseas cambiarlo?`,
    reply_markup: {
      inline_keyboard: [
        [{ text: `‚úÖ Usar ${formatPriceUSD(listBasePrice)}`, callback_data: `${model}|BP|LIST` }],
        [{ text: '‚úèÔ∏è Cambiar precio', callback_data: `${model}|BP|MANUAL` }],
      ],
    },
    isFinished: true,
    isReadyToGenerate: false,
    quotePayload: null,
    userState,
  });
}

if (isBasePick) {
  const [rawModel, , action] = text.split('|');
  const model = sanitizeMachine(rawModel);
  const machine = catalog[model];
  if (!userState || !machine || userState.machine !== model) return output({ text: '‚ö†Ô∏è Sesi√≥n expirada o modelo no coincide. Ejecuta /start.', reply_markup: {}, isFinished: true, isReadyToGenerate: false, quotePayload: null, userState });

  if (action === 'MANUAL') {
    userState.stage = 'await_base_price_manual';
    staticData.cot[chatId] = userState;
    return output({ text: '‚úèÔ∏è Escribe el nuevo *PRECIO BASE* (ejemplo: 16995).', reply_markup: {}, isFinished: true, isReadyToGenerate: false, quotePayload: null, userState });
  }

  userState.basePrice = Number.isFinite(Number(basePriceIndex[model])) ? Number(basePriceIndex[model]) : toNum(machine.basePrice);
  userState.totalPrice = toNum(userState.basePrice);
  userState.stage = 'config';
  staticData.cot[chatId] = userState;
  if (userState.mode === 'FAST') {
    const fastResp = startFastDefaults(userState, machine);
    staticData.cot[chatId] = userState;
    return output({ ...fastResp, isFinished: true, isReadyToGenerate: false, quotePayload: null, userState });
  }
}

if (!isCallback && userState?.stage === 'await_base_price_manual') {
  const manualPrice = toNum(text);
  if (manualPrice <= 0) {
    return output({ text: '‚ö†Ô∏è Precio inv√°lido. Escribe un n√∫mero mayor a 0.', reply_markup: {}, isFinished: true, isReadyToGenerate: false, quotePayload: null, userState });
  }
  userState.basePrice = manualPrice;
  userState.totalPrice = manualPrice;
  userState.stage = 'config';
  staticData.cot[chatId] = userState;
  const machine = catalog[userState.machine];
  if (userState.mode === 'FAST') {
    const fastResp = startFastDefaults(userState, machine);
    staticData.cot[chatId] = userState;
    return output({ ...fastResp, isFinished: true, isReadyToGenerate: false, quotePayload: null, userState });
  }
}

if (isOpt) {
  const [model, , stepId, val, rawPrice] = text.split('|');
  const normalizedModel = sanitizeMachine(model);
  const cleanVal = stripPrice(val);
  const priceFinal = resolvePriceFinal(normalizedModel, stepId, cleanVal, rawPrice);
  if (!userState || userState.machine !== normalizedModel) return output({ text: '‚ö†Ô∏è Sesi√≥n expirada o modelo no coincide. Ejecuta /start.', reply_markup: {}, isFinished: true, isReadyToGenerate: false, quotePayload: null, userState });
  if (userState.mode === 'FAST') return output({ text: '‚ö°Ô∏è Est√°s en Modo R√°pido. Las opciones ya fueron auto-seleccionadas. Env√≠a el nombre del cliente.', reply_markup: {}, isFinished: true, isReadyToGenerate: false, quotePayload: null, userState });
  userState.selections.push({ step: stepId, value: cleanVal, price: priceFinal });
  userState.totalPrice = toNum(userState.totalPrice) + priceFinal;
  userState.stepIndex += 1;
}

if (!isCallback && userState) {
  const msg = normText(text);
  if (userState.stage === 'await_customer_name') {
    userState.customer.name = msg;
    userState.stage = 'await_customer_email';
    staticData.cot[chatId] = userState;
    return output({ text: 'üìù Ahora escribe el *correo del cliente*:', reply_markup: {}, isFinished: true, isReadyToGenerate: false, quotePayload: null, userState });
  }
  if (userState.stage === 'await_customer_email') {
    userState.customer.email = msg;
    userState.stage = 'await_advisor';
    staticData.cot[chatId] = userState;
    return output({ text: 'üßë‚Äçüíº Ahora escribe el *nombre del ASESOR*:', reply_markup: {}, isFinished: true, isReadyToGenerate: false, quotePayload: null, userState });
  }
  if (userState.stage === 'await_advisor') {
    userState.advisor = msg;
    userState.stage = 'done';
    const payload = {
      machine: sanitizeMachine(userState.machine),
      basePrice: toNum(userState.basePrice),
      totalPrice: toNum(userState.totalPrice),
      selections: userState.selections,
      customer: userState.customer,
      advisor: userState.advisor,
      runId: ensureRunId(userState),
    };
    userState.payloadFinal = payload;
    staticData.cot[chatId] = userState;
    const optionLines = formatSelections(userState.selections).join('
');
    const summaryText = `üõ†Ô∏è *Cotizaci√≥n ${userState.machine}*
*M√°quina:* ${userState.machine}
*Base:* ${formatPriceUSD(payload.basePrice)} USD
*Opciones:*
${optionLines}
*Total:* ${formatPriceUSD(payload.totalPrice)} USD
*Cliente:* ${userState.customer.name || 'N/A'}
*Correo:* ${userState.customer.email || 'N/A'}
*Asesor:* ${userState.advisor || 'N/A'}
‚úÖ Confirmo datos. Procediendo a generar y enviar PDF.`;
    return output({ text: summaryText, reply_markup: {}, isFinished: true, isReadyToGenerate: true, quotePayload: payload, userState });
  }
}

if (!userState) return output({ text: 'Usa /start y selecciona una m√°quina para iniciar.', reply_markup: {}, isFinished: true, isReadyToGenerate: false, quotePayload: null, userState: null });
const machineConfig = catalog[userState.machine];
if (!machineConfig) return output({ text: '‚ö†Ô∏è Cat√°logo no disponible. Ejecuta /start para recargar.', reply_markup: {}, isFinished: true, isReadyToGenerate: false, quotePayload: null, userState });

let response = {};
let isFinished = false;

if (userState.stage === 'config') {
  const currentIndex = userState.stepIndex;
  if (currentIndex < machineConfig.steps.length) {
    const step = machineConfig.steps[currentIndex];
    const stepOptions = Array.isArray(step.options) ? step.options : [];
    const inline_keyboard = [];
    const seenOptions = new Set();

    stepOptions.forEach((opt) => {
      const value = opt.value ?? opt.label ?? 'NA';
      const cleanValue = stripPrice(value);
      const key = optionKey(stripPrice(opt.label ?? value), cleanValue);
      if (seenOptions.has(key)) return;
      seenOptions.add(key);

      const baseText = stripPrice(opt.label ?? value);
      const priceFinal = resolvePriceFinal(userState.machine, step.id, cleanValue, opt.price);
      const isDefaultLike = /^default$/i.test(String(cleanValue || '').trim()) || /\(default\)/i.test(String(opt.label || ''));
      const buttonText = isDefaultLike
        ? `${baseText}`.trim()
        : (priceFinal > 0 ? `${baseText} (+${formatPriceUSD(priceFinal)})`.trim() : `${baseText}`.trim());

      inline_keyboard.push([{ text: buttonText, callback_data: `${userState.machine}|OPT|${step.id}|${cleanValue}|${priceFinal}` }]);
    });

    response = {
      text: `üõ†Ô∏è *${userState.machine}* (Paso ${currentIndex + 1}/${machineConfig.steps.length})
üí∞ Total: ${formatPriceUSD(userState.totalPrice)} USD

